import React, { Component } from 'react'
import Web3 from 'web3'

class NftList extends Component {

  async componentWillMount(){
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadBlockchainData(){
    const web3 = window.web3
    const networkId = await web3.eth.net.getId()

    // Load Collectibles
    const chainlinkelf = ChainlinkElf.networks[networkId]
    const chainlinkorc = ChainlinkOrc.networks[networkId]
    const chainlinkknight = chainlinkknight.networks[networkId]


    if(chainlinkelf && chainlinkorc && chainlinkknight){
        const collectible = new web3.eth.Contract(Collectible.abi, erc721.address)




        let URI = await collectible.methods.tokenURI(1).call()
        console.log(URI)
        const response = await fetch(URI);

        const getURL = (collectible) => async (i) => {
          let myContract = await collectible;
          try {
              let val = await myContract.methods.tokenURI(i).call({from:get(selectedAccount)});
              let url = await ipfs_url_from_hash(val);
              let res = await axios.get(url);
              let desc = await res.data.properties.image.description;
              let ret = (await "https://ipfs.io/ipfs/") + desc;
              return ret;
          } catch (err) {
              console.warn("Error: " + err);
          }
      };

        if(!response.ok)
        throw new Error(response.statusText);

        const json = await response.json();
        this.setState({name: json.name})
        this.setState({image: json.image})
        this.setState({description: json.description})

      }else{
        window.alert('NftMarket contract not deployed to detected network')
      }
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected.')
    }
  }

  async listTokensOfOwner({ token: tokenAddress, account }) {
    const token = new web3.eth.Contract(ERC721.abi, tokenAddress, ethers.provider);



  };

  constructor(props){
    super(props)
    this.state = {
      image:{},
      description:'',
      nft:[],
      loading: true
    }
  }

  render() {
    return (
      <div class="container">
        <h2 class="mb-5"> Owned ERC721 </h2>
        <div class="row row-cols-1 row-cols-md-3 mb-3 text-center">
      <div class="col">
        <div class="card mb-4 rounded-3 shadow-sm">
          <div class="card-header py-3">
            <h4 class="my-0 fw-normal">ERC721</h4>
          </div>
          <div class="card-body">
            <h1 class="card-title pricing-card-title">$0<small class="text-muted fw-light">/mo</small></h1>
            <ul class="list-unstyled mt-3 mb-4">
              <li>10 users included</li>
              <li>2 GB of storage</li>
              <li>Email support</li>
              <li>Help center access</li>
              <img src={this.state.image} width="250" height="250"></img>
            </ul>
            <button type="button" class="w-100 btn btn-lg btn-outline-primary">Start Auction</button>
          </div>
        </div>
      </div>
      <div class="col">
        <div class="card mb-4 rounded-3 shadow-sm">
          <div class="card-header py-3">
            <h4 class="my-0 fw-normal">ERC721</h4>
          </div>
          <div class="card-body">
            <h1 class="card-title pricing-card-title">$15<small class="text-muted fw-light">/mo</small></h1>
            <ul class="list-unstyled mt-3 mb-4">
              <li>20 users included</li>
              <li>10 GB of storage</li>
              <li>Priority email support</li>
              <li>Help center access</li>
            </ul>
            <button type="button" class="w-100 btn btn-lg btn-outline-primary">Start Auction</button>
