        const cloJson = await cloResponse.json();
        this.setState({ cloName: cloJson.name })
        this.setState({ cloDescription: cloJson.description })
        this.setState({ cloImage: cloJson.image })

        const clkJson = await clkResponse.json();
        this.setState({ clkName: clkJson.name })
        this.setState({ clkDescription: clkJson.description })
        this.setState({ clkImage: clkJson.image })

      } else {
        window.alert('Nft contracts not deployed to detected network')
      }

    } else if (this.state.userAccount == "") {

    } else if (this.state.userAccount == "") {

    }
    this.setState({loading: false})
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected.')
    }
  }

  constructor(props) {
    super(props);
    this.state = {
      userAccount:'0x0',
      cleUri: '',
      cleName: '',
      cloName: '',
      clkName: '',
      cleImage: {},
      cloImage: {},
      clkImage: {},
      cleDescription: '',
      cloDescription: '',
      clkDescription: '',
      nft: [],
      loading: true
    }
  }

  setInterface = (account) => {
    this.setState({userAccount : account});
  }

  render() {
    let auctionForm
    if (this.state.loading) {
      auctionForm = <p id="loader" classname="text-center">Loading...</p>
    } else {
      auctionForm = <Auction />
    }
    let navbar = <Navbar account={this.state.account}/>
    return (
      <div class="container">
        {navbar}
        <h2 class="mb-5"> Owned ERC721 </h2>
        <div class="row row-cols-1 row-cols-md-3 mb-3 text-center">
          <div class="col">
            <div class="card mb-4 rounded-3 shadow-sm">
              <div class="card-header py-3">
                <h4 class="my-0 fw-normal">{this.state.cleName}</h4>
              </div>
              <div class="card-body">
                <ul class="list-unstyled mt-3 mb-4">
                  <li>{this.state.cleDescription}</li>
                  <img src={this.state.cleImage} width="250" height="250"></img>
                </ul>
              </div>
            </div>
          </div>
        </div>
        {auctionForm}
      </div>
    );
  }
}

export default NftList;
