import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Switch, Link, Redirect } from 'react-router-dom'
import { PropTypes } from 'react'
import Web3 from 'web3'
import ChainlinkElf from "./abis/ChainlinkElf.json"
import ChainlinkOrc from "./abis/ChainlinkOrc.json"
import ChainlinkKnight from "./abis/ChainlinkKnight.json"
import Auction from "./Auction"
import Navbar from "./Navbar"

class NftMarket extends Component {

  async componentDidMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = window.web3
    //const networkId = await web3.eth.net.getId()
    //const accounts = await web3.eth.getAccounts()
    const address = localStorage.getItem('addresss');
    this.setState({userAccount:address});

    // Load Nft Contracts

    if (this.state.userAccount == account1) {
      const chainlinkelf = ChainlinkElf.networks[networkId]
      const chainlinkorc = ChainlinkOrc.networks[networkId]
      const chainlinkknight = ChainlinkKnight.networks[networkId]

      if (chainlinkelf && chainlinkorc && chainlinkknight) {

        const cle = new web3.eth.Contract(ChainlinkElf.abi, chainlinkelf.address)
        const clo = new web3.eth.Contract(ChainlinkOrc.abi, chainlinkorc.address)
        const clk = new web3.eth.Contract(ChainlinkKnight.abi, chainlinkknight.address)

        let cleURI = await cle.methods.tokenURI(1).call()
        this.setState({ cleURI })
        let cloURI = await clo.methods.tokenURI(1).call()
        this.setState({ cloURI })
        let clkURI = await clk.methods.tokenURI(1).call()
        this.setState({ clkURI })

        const cleResponse = await fetch(cleURI);
        const cloResponse = await fetch(cloURI);
        const clkResponse = await fetch(clkURI);

        if (!cleResponse.ok && !cloResponse.ok && !clkResponse.ok)
          throw new Error(response.statusText);

        const cleJson = await cleResponse.json();
        this.setState({ cleName: cleJson.name })
        this.setState({ cleDescription: cleJson.description })
        this.setState({ cleImage: cleJson.image })

        const cloJson = await cloResponse.json();
        this.setState({ cloName: cloJson.name })
        this.setState({ cloDescription: cloJson.description })
        this.setState({ cloImage: cloJson.image })

        const clkJson = await clkResponse.json();
        this.setState({ clkName: clkJson.name })
        this.setState({ clkDescription: clkJson.description })
        this.setState({ clkImage: clkJson.image })

      } else {
        window.alert('Nft contracts not deployed to detected network')
      }

    } else if (this.state.userAccount == "") {

    } else if (this.state.userAccount == "") {

    }

    this.setState({ loading: false })
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected.')
    }
  }

  constructor(props) {
    super(props);
    this.state = {
      userAccount: '0x0',
      cleUri: '',
      cleName: '',
      cloName: '',
      clkName: '',
      cleImage: {},
      cloImage: {},
      clkImage: {},
      cleDescription: '',
      cloDescription: '',
      clkDescription: '',
      loading: true
    }
  }

  render() {
    return (
      <>
      <Navbar address={this.state.userAccount} />
      </>
    );
  }
}

export default NftMarket;
