import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch, Link, Redirect } from 'react-router-dom'
import { PropTypes } from 'react';
import './Login.css';
import NftList from "./NftList";
import Navbar from "./Navbar";

class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      account: '0x0',
      email: "",
      password: ""
    }
    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleEmailChange(event) {
    this.setState({ email: event.target.value });
  }

  handlePasswordChange(event) {
    this.setState({ password: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault()
    if (this.state.email == "owner@owner.com" && this.state.password == "owner123") {
      const userAccount = "0x3e007074340c3a5F2fbd957BDa764051158a3c1e";
      this.state.account = userAccount;
      console.log(this.state.account);
      this.props.history.push('/NftList');

    } else if (this.state.email == "firstbidder@bidder.com" && this.state.password == "bidder123") {
      this.state.userAccount = "0xb74d4838438e472c36Fe617006A882377966c195";

    } else if (this.state.email == "secondbidder@bidder.com" && this.state.password == "bidder123") {
      this.state.userAccount = "0x3e007074340c3a5F2fbd957BDa764051158a3c1e";

    }
    //this.props.history.push('/NftList');
  }

  render() {
    return (
      <div class="container">
        <Navbar address={this.account}/>
        <main class="form-signin">
          <form onSubmit={this.handleSubmit}>
            <h1 class="h3 mb-3 fw-normal">Please sign in</h1>
            <div class="form-floating">
              <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com"
                value={this.state.email} onChange={this.handleEmailChange} />
              <label for="floatingInput">Email address</label>
            </div>
            <div class="form-floating">
              <input type="password" class="form-control" id="floatingPassword" placeholder="Password"
                value={this.state.password} onChange={this.handlePasswordChange} />
              <label for="floatingPassword">Password</label>
            </div>
            <div class="checkbox mb-3">
              <label>
                <input type="checkbox" value="remember-me" /> Remember me
              </label>
            </div>
            <button class="w-100 btn btn-lg btn-primary" type="submit">Sign in</button>
          </form>
        </main>
      </div>
    );
  }
}

export default Login;
