import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Switch, Link, Redirect } from 'react-router-dom'
import { PropTypes } from 'react'
import Web3 from 'web3'
import ChainlinkElf from "./abis/ChainlinkElf.json"
import ChainlinkOrc from "./abis/ChainlinkOrc.json"
import ChainlinkKnight from "./abis/ChainlinkKnight.json"
import Navbar from "./Navbar"
import Nft from "./components/Nft"
import Auction from "./Auction"

class NftMarket extends Component {

  constructor(props) {
    super(props);
    this.state = {
      nftAuction:'',
      auctionPrice:'',
      auctionDays:'',
      auctionHours:'',
      auctionMinutes:'',
      userAccount: '0x0',
      metadata: [],
      loading: true
    }
  }

  async componentDidMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = window.web3
    const networkId = await web3.eth.net.getId()
    console.log(networkId)

    const address = localStorage.getItem('address');
    this.setState({ userAccount: address });
    console.log(this.state.userAccount)

    const account1 = "0x138cd0dF5B11Bf9dda23f04231Bb23db225C6dC3";
    const account2 = "0x4E2E2c34d3118aCc809aD2388D4A551627d0c88c";
    const account3 = "0xCB36091327e0150c7f4D3E5e690C04CAd1C9a768";

    // Load Nft Contracts

    if (this.state.userAccount == account1) {
      const superman = SupermanErc721.networks[networkId]
      const hulk = HulkErc721.networks[networkId]
      const deadpool = DeadpoolErc721.networks[networkId]

      if (superman && hulk && deadpool) {

        superman = new web3.eth.Contract(ChainlinkElf.abi, chainlinkelf.address)
        hulk = new web3.eth.Contract(ChainlinkOrc.abi, chainlinkorc.address)
        deadpool = new web3.eth.Contract(ChainlinkKnight.abi, chainlinkknight.address)

        let cleURI = await cle.methods.tokenURI(1).call()
        let cloURI = await clo.methods.tokenURI(1).call()
        let clkURI = await clk.methods.tokenURI(1).call()

        const cleResponse = await fetch(cleURI);
        const cloResponse = await fetch(cloURI);
        const clkResponse = await fetch(clkURI);

        if (!cleResponse.ok && !cloResponse.ok && !clkResponse.ok)
          throw new Error(response.statusText);

        const cleJson = await cleResponse.json();
        this.state.metadata.push({ id: 0, name: cleJson.name, description: cleJson.description, image: cleJson.image });

        const cloJson = await cloResponse.json();
        this.state.metadata.push({ id: 1, name: cloJson.name, description: cloJson.description, image: cloJson.image });

        const clkJson = await clkResponse.json();
        this.state.metadata.push({ id: 2, name: clkJson.name, description: clkJson.description, image: clkJson.image });

        console.log(this.state.metadata.lastIndex);

      } else {
        window.alert('Nft contracts not deployed to detected network')
      }

    } else if (this.state.userAccount == account2) {

    } else if (this.state.userAccount == account3) {

    }

    this.setState({ loading: false })
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected.')
    }
  }

  handleNftChange(e){
    this.setState({ nftAuction:e.target.value });
  }

  handleAuctionPrice(e){
    this.setState({ auctionPrice:e.target.value });
  }

  handleDayAuction(e){
    this.setState({ auctionDays:e.target.value });
  }

  handleHoursAuction(e){
    this.setState({ auctionHours:e.target.value });
  }

  handleMinutesAuction(e){
    this.setState({ auctionMinutes:e.target.value });
  }

  handleAuctionForm(e){
    e.preventDefault();

  }

  render() {
    return (
      <>
        < Navbar address={this.state.userAccount} />
        <div className="row">
          {this.state.metadata.map(nft => (
            < Nft
              key={nft.id}
              name={nft.name}
              description={nft.description}
              image={nft.image}
            />
          ))}
        </div>
        < Auction />
        <div class="container">
          <h2> ONGOING AUCTION </h2>
        </div>
      </>
    );
  }
}

export default NftMarket;
